import styled from "styled-components";

const Wrapper = styled.section`
.alert {
  padding: 10px;
  border-radius: 4px;
  font-weight: bold;
}

.success {
  background-color: #d4edda;
  color: #155724;
}

.danger {
  background-color: #f8d7da;
  color: #721c24;
}


`;

export default Wrapper;
