import { useSelector } from 'react-redux';
import styled from 'styled-components';

const AlertWrapper = styled.div`
  padding: 10px;
  border-radius: 4px;
  color: #fff;
  font-weight: bold;
  ${({ type }) => type === 'success' && 'background-color: green;'}
  ${({ type }) => type === 'error' && 'background-color: red;'}
  ${({ type }) => type === 'warning' && 'background-color: yellow;'}
`;

const Alert = () => {
  const alertText = useSelector((state) => state.alertText);
  const alertType = useSelector((state) => state.alertType);
  console.log(alertType)

  return (
    <AlertWrapper type={alertType}>
      {alertText}
    </AlertWrapper>
  );
};

export default Alert;
